INSERT INTO `det_eventos` VALUES ('102', '24', 'PARA EL DIA DE HOY ES NOTIFICADO QUE EL DIA DOMINGO 29/12/2019 SE ENCONTRO APAGADO EL REFRIGERADOR QUE CONTIENE LAS VACUNAS EN EL AREA DE CRIA, LO CUAL PODRIA TRAER CONSECUENCIAS YA QUE HAY VACUNAS VIVAS EN SU INTERIOR, SE HACE ACTO DE PRESENCIA EN EL SITIO Y SE MANTIENE ENTREVISTA CON EL PERSONAL TRACTORISTA ALCIDEZ FLORES QUIEN FUE EL QUE REPORTO DICHA NOVEDAD Y SE LE SOLICITA INFORME DESCRIPTIVO DEL SUCESO, TAMBIEN SE LE SOLICITO INFORME DESCRIPTIVO A LOS SUPERVISORES DE CRIA ALI PEREZ Y DENNYS AGUERO PARA COMPROBAR LAS VERSIONES DEL SUCESO, SE PUDO INDAGAR QUE SOLO SE APAGARON LAS LUCES POR PARTE DE LA VIGILANCIA PRIVADA PERO SE PRESUME QUE LOS MISMOS VIGILANTES MANIPULARON LA BRECKERA Y POR TAL MOTIVO SE APAGO EL REFRIGERADOR, QUEDANDO POR PARTE DE LA SUPERVISION DEL AREA DE CRIA Y LEVANTE LA VERIFICACION DEL ENFRIADOR LA CUAL NO FUE REALIZADA AL INICIO DE LA JORNADA DEL DIA DOMINGO. ES DE ACOTAR QUE LAS LUCES FUERON APAGADAS A LAS 06:30 AM Y SE VERIFICO QUE EL ENFRIADOR ESTABA APAGADO A LAS 11:00 AM Y FUE ENCENDIDO A ESA HORA PERO POR NO SER MANIPULADO EL MISMO NO PERDIO MUCHA TEMPERATURA DEL FRIO Y SE MANTUVO LA VACUNA.\r\n');
INSERT INTO `det_eventos` VALUES ('103', '2', 'EVENTO 1:\r\nEL DIA 30/12/2019 LA ANALISTA DE COSTO E INVENTARIO KEILA SALAZAR CI 18.929.508 INFORMA DE MANERA VERBAL AL DIPCP QUE, AL CULMINAR EL INVENTARIO QUE REALIZAN TODOS LOS MESES EN COMPAA DEL SUPERVISOR DE ALMACN Y DESPACHO WILMER CARRASCO CI 20.813.721 AL ALMACN DE LA PLANTA ALIMDOCA DOS,UBICADO EN PIRITU MUNICIPIO ESTELLER ANTIGUO CARGIL DE VENEZUELA.\r\nNOTAN UN FALTANTE DE LOS SIGUIENTES MATERIALES.\r\nA-11 DISCOS DE CORTE DE DIFERENTES \r\n  MEDIDAS.\r\nB-04 DISCOS DE ESMERIL DE DIFERENTE MEDIDAS.\r\nC-06 KILOGRAMOS DE ELECTRODOS.\r\nD- 03 TUBOS DE SILICON.\r\nE- 01 PAQUETE DE PILAS AAA DE 04 UNIDADES\r\n\r\nEVENTO:2\r\nMIENTRAS SE REALIZABA LA INVESTIGACIN DEL EVENTO 1 SE RECIBIO LA NOTIFICACIN DE LA ING MARIA ARIAS SOBRE EL FALTANTE DE EQUIPOS DE PROTECCIN PERSONAL \r\n\r\nA- 03 PARES DE BOTAS DE SEGURIDAD NUEVAS MARCA BRODEKIN.\r\nG-08 RESPIRADORES MEDIA CARA MARCA 3M\r\nB-15 FILTROS PARA POLVO MARCA 3M');
