INSERT INTO `det_eventos` VALUES ('113', '2', 'PARA EL DIA DE HOY 21 DE FEBRERO DEL 2020 ES REPORTADO POR EL INGENIERO JOSE RAFAEL URDANETA JEFE DE OPERACIONES DEL MATADERO MINPOR. EL HURTO DE UNA POCETA UBICADA EN EL PUESTO 3 DE VIGILANCIA, DONDE HACIA EL DIA 30 DE ENERO DEL AO EN CURSO EL PERSONAL DE MANTENIMIENTO DEJA UN MATERIAL QUE SEGUN NO CABE EN EL ALMACEN DE SUMINISTROS Y ES DEJADO BAJO EL RESGUARDO DE LA VIGILANCIA YA QUE TIENEN PERMANENCIA PERENNE EN EL SITIO, SEGUN LA INFORMACION SUMINISTRADA POR EL ING. URDANETA EN EL SITIO HABIA 3 POCETAS NUEVAS Y EL PERSONAL DE VIGILANCIA EL DIA 18/02/2020 REPORTA AL RECIBIR LA GUARDIA QUE FALTA 1 DE LAS POCETAS DE UNO DE LOS BAOS UBICADOS EN LA PERIMETRAL NORESTE DE LAS INSTALACIONES CERCA DEL PUESTO 3, AL MOMENTO NOTIFICAN LA SITUACION AL PERSONAL DE MANTENIMIENTO Y OPERACIONES DEL MATADERO Y PROCEDEN A VERIFICAR Y EN EFECTO FALTA UNA POCETA NUEVA Y LA QUE FALTA EN EL BAO FUE COLOCADA DONDE ESTABAN LAS NUEVAS, SE ABREN LAS AVERIGUACIONES EL DIA 21/02/2020 DEBIDO A QUE LA NOVEDAD FUE REPORTADA ESTE DIA Y NO EL DIA 18/02/2020 POR NINGUNA DE LAS PARTES NI VIGILANCIA NI PERSONAL DEL MATADERO QUIENES INFORMARON QUE HABIAN PASADO UN CORREO EL DIA 20/02/2020.');
INSERT INTO `det_eventos` VALUES ('113', '24', 'ES DE ACOTAR QUE PARA ESTA NOVEDAD EN PARTICULAR NO HUBO UN REPORTE INMEDIATO DEL MISMO SINO QUE SE RECIBE NOTIFICACION 3 DIAS DESPUES YA QUE NI EL PERSONAL SUPERVISORIO DE LA VIGILANCIA NI EL PERSONAL ENCARGADO DE LAS INSTALACIONES NOTIFICO EN EL MOMENTO DEL SUCESO AL PERSONAL DEL DIPCP. ');
INSERT INTO `det_eventos` VALUES ('114', '2', 'ES REPORTADO POR LA INGENIERO ELBA PERALTA QUIEN CUMPLE FUNCIONES COMO ANALISTA DE SEGURIDAD SALUD LABORAL Y AMBIENTE, QUE DEL ALMACEN PRINCIPAL DE LAS INSTALACIONES DE GRANJA LA VIGIA FUERON HURTADAS 1 PAR DE BOTAS DE GOMA CAA ALTA TALLA 45 PROPIEDAD DEL ALMACENISTA RAFAEL LUCENA QUIEN NOTIFICA EL DIA DE HOY QUE EL DIA VIERNES FUE EN LA BUSQUEDA DE LAS BOTAS PARA REALIZAR LABORES EN EL TANQUE  Y NO LAS ENCONTRO, POSTERIOR A ESTO EL DIA LUNES REVISO EN LAS AREAS DEL ALMACEN Y NO ESTAN LAS BOTAS.\r\n');
INSERT INTO `det_eventos` VALUES ('114', '24', 'PARA ESTA NOVEDAD SE REALIZA LA BUSQUEDA DE LAS BOTAS EN TODAS LAS AREAS DONDE SE PUDIERAN HABER ENCONTRADO: AREA DE CRIA Y LEVANTE, PRODUCCION Y ALMACEN PRINCIPAL, SIN EXITO EN LA BUSQUEDA, EN LAS AVERIGUACIONES SE PUDO SABER QUE EL AREA DE LOS LOCKERS QUE ESTABA DENTRO DEL ALMACEN PRINCIPAL FUE CAMBIADO HACIA UNO DE LOS CUARTOS DE LA PARTE EXTERNA DE ALMACEN EL CUAL NO TIENE CERRADURA, NI ALDABA NI SEGURO ALGUNO QUEDANDO TODOS LOS EQUIPOS DE PROTECCION PERSONAL DE FACIL ACCESO A CUALQUIER AGENTE EXTERNO, TAMBIEN SE PUDO SABER QUE ESTE CAMBIO FUE APROXIMADAMENTE HACE 3 SEMANAS Y DESDE ENTONCES EL ALMACENISTA PRESUMIA QUE LAS BOTAS SE ENCONTRABAN EN SU LUGAR. DE IGUAL MANERA ES MUY IMPORTANTE SABER QUE EN LAS INSTALACIONES DE LA GRANJA SOLO HAY 2 PARES DE BOTAS DE LA TALLA 45 POR LO CUAL ES MUY DIFICIL QUE SE HAYAN CONFUNDIDO CON LAS BOTAS DE ALGUN OTRO PERSONAL.');
INSERT INTO `det_eventos` VALUES ('115', '24', 'PARA EL DA 30 /01/2020 ES REPORTADA NOVEDAD EN GRANJA USERA POR PARTE DE LA ING. LOURDES UMBRA NOTIFICA QUE EL SR. JOS TORRES QUIEN SE DESEMPEA COMO PLANIFICADOR DE MANTENIMIENTO, ENCONTR EN LA CASETA UBICADA AL LADO DE LOS SILOS DE ALIMENTACIN UN MATERIAL DE ELECTRICIDAD Y PLOMERA DEBAJO DE UNA CAMA DONDE PERNOCTAN LOS NOCHEROS QUE LABORAN EN LA GRANJA, SE CONVERS CON EL JEFE DE GRUPO DE GUARDIA DE LA CONTRATISTA DE SEGURIDAD SR. JOS RODRGUEZ DE 29 AOS DE EDAD PORTADOR DE LA CDULA 24.927.700 RESIDENCIADO EN EL SECTOR EL POTRERO DE BOBARE, QUIEN MANIFEST QUE EL MATERIAL ENCONTRADO ESTABA EN ESE SITIO DESDE HACE MS DE 1 AO  DEL CUAL EL SUP. DAVID CAMACARO ESTABA EN CUENTA DE DICHO MATERIAL, TODA ESTA INFORMACIN QUEDA BAJO CONSTANCIA EN INFORME DESCRIPTIVO SOLICITADO AL VIGILANTE. ES DE ACOTAR QUE EL MATERIAL ENCONTRADO FUE COLOCADO EN 1 DE LOS CONTENEDORES PARA SU RESGUARDO POR EL PERSONAL DE MANTENIMIENTO.\r\nDE IGUAL MANERA Y APROVECHANDO LA SITUACIN SE PROCEDI A VERIFICAR EL ESTADO DE LA GRANJA SE EVIDENCIA GRAN CANTIDAD DE FALLAS, TAL COMO LA FALTA DE 1 ALA DEL PORTN DE INGRESO AL TNEL DE DESINFECCIN, GRAN CANTIDAD DE MATERIAL DE CONSTRUCCIN DEJADO EN EL REA DEL TNEL DE DESINFECCIN SIN RESGUARDO, ESTO DEBERA ESTAR BAJO RESGUARDO E INVENTARIADO PARA DEJAR LA TRAZABILIDAD DE TODOS ESTOS MATERIALES.\r\n\r\n');
