INSERT INTO `reporte_diario` VALUES ('0000000163', '17', '11', '2019-10-16', 'Hetler Colmenarez', '1- se realiza visita a la planta EDMA donde se verifica la recepcion de maiz humedo y el precintaje de los mismo , se verifica las condiciones de riesgo de la planta.', 'Sergio Gomez', 'jede ');
