INSERT INTO `reporte_diario` VALUES ('0000000196', '1', '11', '2019-10-29', 'integrantes del departamento', 'Reunin de planificacin del departamento con el Doctor Miguel Andueza hora de salida 12.00 del medioda', 'Miguel Andueza', 'jefe');
INSERT INTO `reporte_diario` VALUES ('0000000197', '4', '11', '2019-10-29', 'Hether Colmenarez', 'se hace supervision al personal de vigilancia encontrando los dos vigilantes de guardia, realizaron un buen control de acceso todo sin novedad.se da recorrido por la avicola ', 'Nolberto Segura', 'jefe de avicola');
INSERT INTO `reporte_diario` VALUES ('0000000198', '1', '11', '2019-10-31', 'Hether Colmenarez', 'se sostiene reunion con directivos de toguman donde los mismos nos presentan al nuevo jefe de operaciones para la zona de portuguesa', 'Leonardo torres', 'jedfe de pcp');
INSERT INTO `reporte_diario` VALUES ('0000000199', '12', '11', '2019-11-04', 'Coord Hether Colmenarez', 'Se realiza inspeccion y recorrido , se cierra reporte de eventos sobre caso de hurto ocurrido el dia 27/10/2019 a la hora de la visita personal de vigilancia completo', 'Cristina Montes', 'jefe de planta');
INSERT INTO `reporte_diario` VALUES ('0000000200', '7', '11', '2019-11-05', 'Jesus Torrealba', 'se da recorrido por la avicola y el coordinador Jesus Torrealba hace entrega de las novedades y de las sugerencias y procesos a implementar en la avicola', 'Pedro Alvarez', 'jefe de avicola');
INSERT INTO `reporte_diario` VALUES ('0000000201', '8', '12', '2019-11-06', '', 'Se hizo presencia en la sede de Transporte a fin de que se realizara mantenimiento a la camioneta asignada: Reparacin de tubo de escape, rotacin y alineacin de cauchos.\r\n\r\nEn la tarde, se estuvo en el taller Serwica para cambio de aceite.', 'Moises Soto', 'Gerente');
INSERT INTO `reporte_diario` VALUES ('0000000202', '1', '12', '2019-11-05', '', 'Se observ deficiencia en el control de acceso de personas y de vehculos. Se convers con el Sup. Labastida (DISECA), quien se encontraba en las instalaciones, a fin de que se tomarn las medidas correctivas con el personal de vigilancia.', 'Miguel Andueza', 'Gerente');
INSERT INTO `reporte_diario` VALUES ('0000000203', '19', '12', '2019-11-01', 'ENDER ESCALONA Y JOEUDIS PIA', 'SE REALIZ SUPERVISIN A LAS INSTALACIONES DE LA PLANTA ABA Y EN SILOS TUREN, CONSTATNDOSE LAS MISMAS SIN NOVEDAD, LA OPERACIN DE RECEPCIN DE MAZ DE COSECHA SE OBSERV EN OPERACIN NORMAL.\r\n\r\nSE CONT CON LA COMPAA DEL PERSONAL SUPERVISORIO DE LA CONTRATISTA TOGUMAN QUIENES SE ENTREVISTARON CON EL PERSONAL DE VIGILANCIA Y REALIZARON LA DOTACIN DE UNIFORMES Y CHALECOS REFLECTIVOS.', 'LUIS LOVERA', 'JEFE DE PLANTA');
