INSERT INTO `reporte_diario` VALUES ('0000000312', '8', '11', '2019-12-07', '', 'se da recorrido por el transporte y se encontr de guardia a dos vigilantes, se verifico las puertas de las oficinas y el rea del almacn y se evidencio todo cerrado y en normalidad ', 'Moises Soto', 'jefe de transporte');
