INSERT INTO `reporte_diario` VALUES ('0000000348', '19', '11', '2019-12-18', 'LUIS LOVERA', 'SE SOSTIENE REUNIN CON EL ING LUIS LOVERA QUIEN MANIFIESTA QUE YA SERN COLOCADAS LAS CONCERTINAS  POR LA PARED PERIMETRAL QUE ESTA UBICADA CERCA DE LOS TANQUES DE ACEITE DE REA DE PRODUCCIN, TAMBIN ME INFORMO QUE YA CANALIZO LA COLOCACIN DE UN REFLECTOR QUE ILUMINARA TODA LA ZONA DE LA ENTRADA A LA PLANTA FACILITANDO ESTO LA VISUAL PARA LOS VIGILANTES QUE SE ENCUENTREN ES CUSTODIA.SE APROVECHO Y SE LE SOLICITO LAS FIRMAS PARA LOS REPORTE REALIZADOS POR LOS COORDINADORES EN DICHA PLANTA', 'LUIS LOVERA', 'JEFE DE PLANTA');
INSERT INTO `reporte_diario` VALUES ('0000000349', '19', '11', '2019-12-19', 'ENDER ESCALONA TOGUMAN', 'SE ATIENDE SITUACIN CON LOS VIGILANTES DE GUARDIA QUIENES PRETENDAN ABANDONAR EL SERVICIO, CON LA SOLICITUD DE QUE LES AUMENTARAN EL SUELDO, SE HABLO CON EL GRUPO COMPLETO DE VIGILANTES Y SE RETIRARON DE LA PLANTA A LOS SEORES JOSE AMARO Y LUIS LAMEDA QUIENES MANIFESTARON NO ESTAR CONFORME CON EL PAGO QUE RECIBEN DE LA EMPRESA TOGUMAN, DICHA SITUACIN SE LE REPORTA AL JEFE DE PCP LEONARDO TORRES Y A LOS DIRECTIVOS DE LA EMPRESA CONTRATISTA TOGUMAN', 'LUIS LOVERA', 'JEFE DE PLANTA');
INSERT INTO `reporte_diario` VALUES ('0000000350', '15', '11', '2019-12-17', 'SUP JONATHAN DE TOGUMAN', 'SE DA RECORRIDO POR LA PLANTA Y SE EVIDENCIA QUE EXISTE FUGA DE ARROZ PADDY DEL SILO PLANO YA QUE EL MISMO QUEDO EN SU MXIMA CAPACIDAD DE ALMACENAMIENTO, ESTA SITUACIN LE FUE REPORTADA A LA ING MARIA ARIAS Y LA MISMA INDICO ESTAR AL TANTO DE LA SITUACIN Y DAR LA ORDEN DE QUE EL ARROZ QUE FILTRE DEL SILO PLANO SEA RECOGIDO DIARIAMENTE.\r\nSE LE ENTREGO LA CARPETA CON LOS REPORTES DEL DIPCP A LA ING MARIA ARIAS PARA QUE FIRMARA LOS MISMOS \r\n\r\n\r\n', 'MARIA ARIAS', 'JEFE DE PLANTA');
INSERT INTO `reporte_diario` VALUES ('0000000351', '17', '11', '2019-12-18', '', 'SE DA RECORRIDO POR LAS INSTALACIONES Y SE LE ENTREGA LA CARPETA CON LOS REPORTES DEL DIPCP AL ING SERGIO GOMEZ PARA QUE LOS REVISE Y LUEGO LES COLOQUE LAS FIRMAS REQUERIDAS.\r\nSE REVISA EL LLENADO DE LOS FORMATOS DE CONTROL DEL DEPARTAMENTO Y SE LES DA INSTRUCCIONES A LOS VIGILANTES EN CUANTO AL CONTROL DE ACCESO DE PERSONAS Y VEHCULOS', 'SEGIO GOMEZ', 'JEFE DE UNIDAD');
INSERT INTO `reporte_diario` VALUES ('0000000352', '17', '11', '2019-12-19', 'ALEXIS RIERA', 'SE PROCEDE A LA REVISIN, CUBICADO Y COLOCACIN DE PRECINTOS  DE LOS SILOS VERTICALES  Y EL SILO HORIZONTAL DE LA AGROPECUARIA EDMA, YA QUE EL PERSONAL DE DICHA AGROPECUARIA CIERRA SUS OPERACIONES HASTA EL 16/01/2020.SE ESTABLECE UN PROCEDIMIENTO DE PRECINTADO A LA PUERTA PEATONAL DEL SILO PLANO YA QUE EL MISMO SERA ABIERTO EN 06 OCASIONES PARA SU RESPECTIVA FUMIGACION, EN LA FUMIGACION ESTARN PRESENTE PERSONAL DE AGROPECUARIA EDMA Y PERSONAL PCP DE AGROINSUMOS LARA', 'SERGIO GOMEZ', 'JEFE DE UNIDAD');
