INSERT INTO `reporte_diario` VALUES ('0000000479', '14', '12', '2020-02-05', 'CORRD. JAIRO OVIEDO', 'SE REALIZA OPERATIVO NOCTURNO EN LAS INSTALACIONES DE LA UNIDAD DE PRODUCCIN, A FIN DE HACER DETECCIN EN LA GESTIN DE LA CONTRATISTA DE SEGURIDAD, LA PLANTILLA DE VIGILANTES SE VERIFIC COMPLETA. AL MOMENTO DE LA LLEGADA A LA PLANTA SE OBSERV 2 VEHCULOS DE CARGA (CONCHA) ESTACIONADO DENTRO DE LA UNIDAD, SE REALIZ COMUNICACIN CON EL ING. JEAN QUINTANA E INFORM QUE SE ENCONTRABAN AUTORIZADOS. SE REALIZ RECORRIDO POR LAS INSTALACIONES Y SE VERIFIC LOS PRECINTOS INSTALADOS. SE HIZO DETECCIONES EN LA DOTACIN DE LOS VIGILANTES.', 'MARIA JOSE ARIAS', 'JEFE DE PLANTA');
INSERT INTO `reporte_diario` VALUES ('0000000480', '15', '12', '2020-02-05', 'COORD. JAIRO OVIEDO', 'SE REALIZA OPERATIVO NOCTURNO EN LAS INSTALACIONES DE LA UNIDAD DE PRODUCCIN, A FIN DE HACER DETECCIN EN LA GESTIN DE LA CONTRATISTA DE SEGURIDAD, LA PLANTILLA DE VIGILANTES SE VERIFIC COMPLETA. SE REALIZ RECORRIDO POR LAS INSTALACIONES Y SE VERIFIC LAS MISMAS SIN NOVEDAD. SE OBSERV UN OBRERO QUIEN NO PORTABA UNIFORME Y SE LE HIZO LA OBSERVACIN DE LA OBLIGACIN EN EL USO DEL MISMO. SE LE DIO INSTRUCCIONES AL PERSONAL DE VIGILANCIA EN RELACIN AL DISPOSITIVO DE SEGURIDAD A IMPLEMENTAR EN HORAS NOCTURNAS. SE CONVERS CON LA SUPERVISORA DE TURNO ELIANNY EN RELACIN A QUE DEBE COMUNICAR AL PERSONAL DEL DIPCP LAS OBSERVACIONES EN LA GESTIN DE LOS VIGILANTES. SE HIZO DETECCIONES EN LA DOTACIN DE LOS VIGILANTES. SE HIZO ACOMPAAMIENTO EN EL CIERRE DE PLANTA', 'MARIA JOS ARIAS', 'JEFE DE PLANTA');
