INSERT INTO `reporte_diario` VALUES ('0000000548', '7', '14', '2020-03-02', 'JHONNY LABASTIDAS', 'SE REALIZA SUPERVISION DEL PERSONAL DE VIGILANCIA Y SE REALIZAN REUNIONES CON EL PERSONAL DEL GRUPO SALIENTE Y ENTRANTE A LAS GUARDIAS EN RELACION A LAS REACCIONES DEL DISPOSITIVO DE SEGURIDAD ACA EN LA GRANJA LA VIGIA  AL MOMENTO DE UNA EVENTUALIDAD EN HORAS NOCTURNAS, YA QUE EL DIA DOMINGO EN HORAS DE LA NOCHE SE REALIZO UNA PRUEBA DE VULNERABILIDAD, A LO QUE EL PERSONAL DE VIGILANCIA NO TUVO UNA REACCION POSITIVA, YA QUE SE INGRESO A LA GRANJA Y NO HUBO COMUNICACION ALGUNA POR PARTE DEL PERSONAL DE LA VIGILANCIA MIENTRAS SE ESTUVO DANDO RONDAS EN EL INTERIOR DE LA MISMA, DE IGUAL  MANERA SE LES REALIZA FUERTE LLAMADO DE ATENCION POR  QUE SON SITUACIONES QUE NO DEBEN OCURRIR.\r\n TAL CUAL COMO EL DIA DE  HOY MAANA SE ESTARA CONVERSANDO CON EL GRUPO DE GUARDIA PARA DAR LAS INDICACIONES Y CORRECTIVOS DEL CASO CON EL SUPERVISOR DE LA CONTRATISTA Y EL PERSONAL QUE ESTE DE GUARDIA. \r\n\r\nSE LEVANTA REPORTE DE EVENTO EN RELACION AL PRESUNTO HURTO DE UNAS BOTAS CAA ALTA TALLA 45 PROPIEDAD DEL ALMACENISTA RAFAEL LUCENA', 'PEDRO ALVAREZ', 'JEFE DE GRANJA ');
