INSERT INTO `reporte_eventos` VALUES ('0000000042', '19', '11', '2018-11-26', '0', 'Se coloca la denuncia de lo ocurrido ante el CICPC K-18-0058-01181.<br>\r\nEl CICPC realiza 06 detenciones de sujetos involucrados en el hecho.<br>', 'Se realiza fijacin fotografica del hecho.<br>\r\nSe recupera el producto del silo plano.<br>', 'Se recomienda a los jefes de planta realizar cierres de instalaciones personalmente y no delegar dicho trabajo a los vigilantes.<br>\r\nCambio de los vigilantes que estaban de guardia el dia del hecho.<br>\r\nSe le solicito a la empresa contratista TOGUMAN un mayor control y verificacion de datos al personal que van a contratar para prestar servicios en nuestras instalaciones.<br>\r\n\r\nReporte realizado por el coordinador PCP Jairo Oviedo y Transcrito por el coordinador PCP Jess Torrealba.<br> ', 'Leonardo Torres ', 'Jefe DIPCP');
