INSERT INTO `det_eventos` VALUES ('100', '18', 'AL MOMENTO DE LA DETECCIÓN DEL INTENTO DE HURTO, SE RECUPERÓ 15 REJILLAS (BANDEJAS PORTA CABLES) METÁLICAS.\r\nREFIERE EL ING. ELIS VARGAS, DEL DPTO. DE MTTO, QUE LAS REJILLAS SE ENCONTRABAN EN EL ÁREA EXTERIOR Y ADYACENTE AL CUARTO (DEPÓSITO) UBICADO JUNTO AL PORTÓN DE ACCESO AL ÁREA DE PRODUCCIÓN, PERO DENTRO DEL PERÍMETRO DE PRODUCCIÓN. YA QUE EN ESE LUGAR, LOS ELECTROMECÁNICOS ESTÁN REALIZANDO LA REPARACIÓN Y/O DESCARTES DE LAS MISMAS.\r\nLAS REJILLAS SON UTILIZADAS EN EL GALPÓN DE PRODUCCIÓN, ESPECÍFICAMENTE EN LA PARTE SUPERIOR DE LAS JAULAS, POR DONDE SE DESPLAZA EL CARRO DISTRIBUIDOR DE ALIMENTOS PARA AVES.');
