INSERT INTO `det_eventos` VALUES ('109', '24', 'EL DÍA MIÉRCOLES 29/01/2020, LA ING. CRISTINA MONTES SE COMUNICA VÍA TELEFÓNICA CON EL COORD. JAIRO OVIEDO, NOTIFICANDO QUE ESE DÍA AL MOMENTO DE LLEGAR A LA PLANTA ALIMDOCA PAYARA,  PUDO OBSERVAR QUE EN EL ÁREA DE DESPACHO DE CONCHA DE ARROZ, EL TANQUE 1, HABÍA DESCARGADO AL PISO UNA CANTIDAD IMPORTANTE (RUMA) DE CONCHA DE ARROZ, MANIFESTÓ QUE SE HABÍA COMUNICADO CON EL SUP. MOISES ROMANO DE LA VIGILANCIA.\r\nEL COORD. JAIRO OVIEDO SEGUIDAMENTE SE COMUNICÓ CON EL SUP. MOISES ROMANO Y LE MANIFESTÓ QUE HABÍA SIDO REPORTADO DE LA NOVEDAD POR EL EQUIPO DE VIGILANCIA DE GUARDIA 24 HORAS DE 28 A 29/02/2020, PERO QUE NO LE HABÍA REPORTADO DE LA MISMA AL EQUIPO DE PCP.\r\nEL COORD. JAIRO OVIEDO LE SOLICITÓ AL SUP. MOISES ROMANO, QUE LOS VIGILANTES DEBÍAN REALIZAR REPORTE POR ESCRITO DE LA NOVEDAD.');
INSERT INTO `det_eventos` VALUES ('110', '9', 'SIENDO LAS 11:10 HORAS, SE RECIBE NOTIFICACIÓN DE EVENTO POR PARTE DEL SR. LEONARDO TORRES, INFORMANDO QUE EXISTE UNA NOVEDAD EN LA SEDE DE AGROINSUMOS LARA, ZONA II, YA QUE FUE INFORMADO DE QUE LA PUERTA DE VIDRIO DE LA OFICINA DE PRESIDENCIA, FUE VIOLENTADA Y SE ENCUENTRA PARTIDA.\r\nUNA VEZ EN EL LUGAR, SE CONSTATÓ LA PRESENCIA DEL SR. LEONARDO TORRES Y DEL ADMINISTRADOR DEL GRUPO LIC. JAIR, ASIMISMO  SE PUDO VERIFICAR LA NOVEDAD EXISTENTE DEL DAÑO EN LA PUERTA.\r\nEL SR. LEONARDO TORRES, PROCEDIÓ A SOLICITAR EL REGISTRO VIDEOGRÁFICO DEL SISTEMA DE CCTV A FIN DE VERIFICAR LA CAUSA O RESPONSABLE DEL DAÑO A LA PUERTA DE VIDRIO.');
