INSERT INTO `det_eventos` VALUES ('111', '2', 'PARA EL DÍA 08-02-2020 EN EL PROCESO DE VACUNACION Y RECONTEO DE POLLAS EN GALPÓN #3 SE OBSERVA QUE AL FINALIZAR LA VACUNACION SEGÚN CONTEO HAY UN FALTANTE DE POLLAS, SE PROCEDE AL RECONTEO EN LOS GALPONES #2 Y #3 RESPECTIVAMENTE Y SE EVIDENCIA LA FALTA  DE  840 POLLAS SEGUN EL ULTIMO CONTEO DE 5882 POLLAS, QUEDANDO EN EXISTENCIA 5042 POLLAS, SE SOLICITA INFORME DESCRIPTIVO AL ING. NORBERTO SEGURA PARA TENER CONSTANCIA DE LA CANTIDAD DE POLLAS EN CADA GALPON. DE IGUAL MANERA EL DIA LUNES 10-02-2020 AL MOMENTO DE COMENZAR LAS OPERACIONES LE ES REPORTADO AL COORDINADOR JAIRO OVIEDO UNAS HUELLAS Y MARCAS EN LA PARTE TRASERA DEL GALPON #3, SE PROCEDE AL CONTEO DEL GALPON NUEVAMENTE Y SE PUDO EVIDENCIAR QUE HABIA UN FALTANTE DE 170 POLLAS, SE PROCEDE A LEVANTAR LA DENUNCIA EN EL CICPC Y SE ABREN LAS AVERIGUACIONES PERTINENTES.');
