INSERT INTO `reporte_diario` VALUES ('0000000148', '11', '11', '2019-10-11', '', '1- se realiza reporte de riesgo de la planta aba turen y se le hace entrega al ing Luis Lovera para que dicha condición sea discutida en la  mesa de trabajo que se lleva acabo en planta Aba Araure.\r\n2- se da recorrido por la planta encontrando todo sin novedad.', 'Ivonne Rodriguez', 'jefe de planta');
INSERT INTO `reporte_diario` VALUES ('0000000149', '15', '11', '2019-10-09', 'Hetler Colmenarez', '1- se le presta apoyo al Ing Douglas Contreras para trasladar EPP hasta alimdoca  2\r\n2-se realiza recorrido por la planta encontrando la plantillas de vigilantes completos y sin novedad', 'Maria Arias', 'jefe de planta');
INSERT INTO `reporte_diario` VALUES ('0000000150', '7', '11', '2019-10-09', 'COORD Jesus Torrealba', '1- se presta el apoyo de trasladar  a personal de costo e inventario hasta la avicola\r\n2- se realiza revisión sorpresa a los bolsos de vigilantes del turno saliente.\r\n3- se hace recorrido por toda la avicola y se les da recordatorio a los vigilantes sobre el llenado de los formatos de control', 'Pedro Alvarez', 'jefe de granja');
INSERT INTO `reporte_diario` VALUES ('0000000151', '6', '11', '2019-10-09', '', '1- se da recorrido por toda el area encontrando todo sin novedad\r\n2- se les da recordatorio a los vigilantes sobre las normas de control de acceso a las instalaciones.\r\n3-a la hora de la supervision la plantilla de vigilantes completa', 'Moraima Adans', 'jefe de matadero');
INSERT INTO `reporte_diario` VALUES ('0000000152', '9', '11', '2019-10-10', '', '1- se realiza descarte de ciento siete (107) cajas de huevo entre tipo A, jumbo  y tipo Granja se deja evidencia fotográfica del descarte y actas de dicho descarte o desecho de inventario', 'Nailet Rodriguez', 'jefe de almacen');
INSERT INTO `reporte_diario` VALUES ('0000000153', '4', '12', '2019-10-12', '', 'SE REALIZÓ SUPERVISIÓN A LAS INSTALACIONES DE LA UNIDAD DE PRODUCCIÓN, SE CONVERSÓ CON EL PERSONAL DE VIGILANCIA Y SE LES RECORDÓ ASPECTO BÁSICOS SOBRE EL DISPOSITIVO DE SEGURIDAD. SE DETECTÓ FALLAS EN LOS CONTROLES DE ACCESO.', 'NORBERTO SEGURA', 'JEFE DE GRANJA');
