INSERT INTO `reporte_diario` VALUES ('0000000219', '7', '11', '2019-11-06', 'Jesus Torrealba coord', '1-se levanta reporte de riesgo sugiriendo formato de check list en las areas de almacén, galpon de cría y galpones de produccion.\r\n2- se abre averiguaciones sobre reporte de llave de paso desaparecida del dique de contención de los tanques de combustible diesel.\r\n3-se atiende incidente ocurrido con el chófer Margaro Hernandez quien con el vehiculo reventó un cableado electrico a una vivienda en la poblacion de la vigia', 'Pedro Alvarez', 'jefe de avicola');
