INSERT INTO `reporte_diario` VALUES ('0000000260', '7', '11', '2019-11-13', '', '1- se revisan los listado de personal obrero e ingenieros enviados por la contratista Miguez \r\n2- se supervisa el trabajo de los vigilantes de la entrada principal y se les recuerda que solo se les dará entrada a las personas que estén en la lista enviada por la contratista de lo contrario permanecerán del lado de afuera de avicola\r\n3- se da recorrido por toda la avicola\r\n', 'Pedro Alvarez', 'jefe de granja');
