INSERT INTO `reporte_diario` VALUES ('0000000263', '19', '12', '2019-11-14', '', 'SE REALIZÓ SUPERVISIÓN A LA PLANTILLA DE VIGILANTES EN SILOS TUREN Y EN ABA TUREN, SE ATENDIÓ CASO DE SUSTRACCIÓN NO AUTORIZADA DE MAIZ DE COSECHA DE 1 GANDOLA QUE ESPERABA EN LA PARTE EXTERNA DE LA PLANTA PARA REPESAJE. SE REALIZÓ EL DECOMISO DEL 1 SACO DE MAÍZ Y 1/4 DE SACO DE MAÍZ. SE MANTUVO REUNIÓN CON EL JEFE DE PLANTA. SE REALIZÓ REUNIÓN CON EL SUPERVISOR Y CON EL JEFE DE GRUPO Y SU AYUDANTE, TODOS DE LA CONTRATISTA DE SEGURDIAD, EN RELACIÓN AL EVENTO DETECTADO', 'LUIS LOVERA', 'JEFE DE PLANTA');
INSERT INTO `reporte_diario` VALUES ('0000000264', '7', '11', '2019-11-16', '', '1- se realiza supervision hoy sábado encontrando todo el personal de vigilancia completo\r\n2- las contratistas de seguridad realizando trabajo en la construcción de los galpones ', 'Pedro Alvarez', 'jefe de granja');
