INSERT INTO `reporte_diario` VALUES ('0000000319', '3', '12', '2019-12-06', '', 'SE REALIZÓ SUPERVISIÓN A LAS INSTALACIONES, SE REVISÓ LA PLANTILLA DE VIGILANTES DE GUARDIA, SE MANTUVO REUNIÓN CON EL DR. EDGARDO MORÓN Y EL PERSONAL DE MTTO DE GRANJA EN RELACIÓN A EVALUAR LAS CONDICIONES Y DISPONIBILIDADES PARA LA FABRICACIÓN DE GARITAS DE VIGILANCIA, QUIENES MANIFESTARON ESTAR EN LA DISPOSICIÓN DE OFRECER TODOS LOS MATERIALES DISPONIBLES EN LA GRANJA PARA LA CONSTRUCCIÓN DE LAS MISMAS YA QUE SE REQUIERE CON URGENCIA.\r\n\r\nSE HIZO LA INSPECCIÓN EN LOS LUGARES DONDE SE REQUIERE LA INSTALACIÓN Y CONSTRUCCIÓN DE LAS GARITAS', 'EDGARDO MORÓN', 'JEFE DE GRANJA');
