INSERT INTO `reporte_diario` VALUES ('0000000350', '15', '11', '2019-12-17', 'SUP JONATHAN DE TOGUMAN', 'SE DA RECORRIDO POR LA PLANTA Y SE EVIDENCIA QUE EXISTE FUGA DE ARROZ PADDY DEL SILO PLANO YA QUE EL MISMO QUEDO EN SU MÁXIMA CAPACIDAD DE ALMACENAMIENTO, ESTA SITUACIÓN LE FUE REPORTADA A LA ING MARIA ARIAS Y LA MISMA INDICO ESTAR AL TANTO DE LA SITUACIÓN Y DAR LA ORDEN DE QUE EL ARROZ QUE FILTRE DEL SILO PLANO SEA RECOGIDO DIARIAMENTE.\r\nSE LE ENTREGO LA CARPETA CON LOS REPORTES DEL DIPCP A LA ING MARIA ARIAS PARA QUE FIRMARA LOS MISMOS \r\n\r\n\r\n', 'MARIA ARIAS', 'JEFE DE PLANTA');
