INSERT INTO `reporte_diario` VALUES ('0000000352', '17', '11', '2019-12-19', 'ALEXIS RIERA', 'SE PROCEDE A LA REVISIÓN, CUBICADO Y COLOCACIÓN DE PRECINTOS  DE LOS SILOS VERTICALES  Y EL SILO HORIZONTAL DE LA AGROPECUARIA EDMA, YA QUE EL PERSONAL DE DICHA AGROPECUARIA CIERRA SUS OPERACIONES HASTA EL 16/01/2020.SE ESTABLECE UN PROCEDIMIENTO DE PRECINTADO A LA PUERTA PEATONAL DEL SILO PLANO YA QUE EL MISMO SERA ABIERTO EN 06 OCASIONES PARA SU RESPECTIVA FUMIGACION, EN LA FUMIGACION ESTARÁN PRESENTE PERSONAL DE AGROPECUARIA EDMA Y PERSONAL PCP DE AGROINSUMOS LARA', 'SERGIO GOMEZ', 'JEFE DE UNIDAD');
